(2, 1, '2021-03-18 13:59:31', '2021-03-18 13:59:31', '<!-- wp:acf/hero {\n    \"id\": \"block_60576352e833d\",\n    \"name\": \"acf\\/hero\",\n    \"data\": {\n        \"bg_image\": 31,\n        \"_bg_image\": \"field_60576299b8004\",\n        \"heading\": \"Tecala’s fully flexible and \\r\\nsecure <span>Cloud Services.<\\/span>\",\n        \"_heading\": \"field_605762a6b8005\",\n        \"subheading\": \"Now you can pay as you grow with complete \\r\\nfreedom, flexibility and control in the Cloud\",\n        \"_subheading\": \"field_605762b8b8006\"\n    },\n    \"align\": \"\",\n    \"mode\": \"preview\"\n} /-->\n\n<!-- wp:acf/section {\n    \"id\": \"block_60576859f2b8b\",\n    \"name\": \"acf\\/section\",\n    \"data\": {\n        \"section\": \"AREA OF EXPERTISE\",\n        \"_section\": \"field_6057670570df0\",\n        \"bg_image\": \"\",\n        \"_bg_image\": \"field_6057671870df1\",\n        \"heading\": \"We make cloud services simple and straightforward. \",\n        \"_heading\": \"field_6057672970df2\",\n        \"subheading\": \"Whether you need assistance moving to the cloud or you need a plan to level up, we can help.\",\n        \"_subheading\": \"field_6057674570df3\",\n        \"details\": \"We roadmap and deliver business-appropriate XaaS solutions for your organisation without single-cloud bias, taking the best from all cloud vendors and providers to deliver the right service for your business. As your business grows and evolves over the long term, we respond to keep you operating at peak performance.\\r\\n\\r\\n<span>We specialise in:<\\/span>\\r\\n\",\n        \"_details\": \"field_6057694c28eb2\"\n    },\n    \"align\": \"\",\n    \"mode\": \"preview\"\n} /-->\n\n<!-- wp:acf/boxes {\n    \"id\": \"block_60576cff1cfd5\",\n    \"name\": \"acf\\/boxes\",\n    \"data\": {\n        \"box_0_image\": 43,\n        \"_box_0_image\": \"field_60576806ea7c3\",\n        \"box_0_label\": \"\",\n        \"_box_0_label\": \"field_6057681768374\",\n        \